import React from "react";
import { useState, useEffect } from "react";

const PasswordShowInput = ({ name, disabled, value, setValue }) => {
	const [show, setShow] = useState(false);

	const onShow = (e) => {
		e.preventDefault();
		setShow(!show);
	};

	useEffect(() => {
		setShow(false);
		return () => {
			setShow(false);
		};
	}, [disabled]);

	return (
		<div className="showpassword">
			<input
				disabled={disabled}
				name={name}
				type={show && !disabled ? "text" : "password"}
				value={value}
				onChange={(e) => setValue(e.target.value)}
			/>
			{!disabled ? (
				<p onClick={onShow}>
					<i class={!show ? "fa-solid fa-eye" : "fa-solid  fa-eye-slash"}></i>
				</p>
			) : null}
		</div>
	);
};

export default PasswordShowInput;
